# Define the camera range A in ROS coordinates
def move_camera_range(A, point):
    dx = point[0]
    dy = point[1]
    A_moved = [(a[0] + dx, a[1] + dy) for a in A]
    return A_moved

# Check if a point is inside the polygon defined by A
def is_inside_polygon(pt, poly):
    x, y = pt
    oddNodes = False
    j = len(poly) - 1  # The last vertex is the 'previous' one to start with

    for i in range(len(poly)):
        xi, yi = poly[i]
        xj, yj = poly[j]
        if yi < y and yj >= y or yj < y and yi >= y:
            if xi + (y - yi) / (yj - yi) * (xj - xi) < x:
                oddNodes = not oddNodes
        j = i

    return oddNodes

